package org.shecodes.course.aws_cloud.ifat.model.dto;

import lombok.AllArgsConstructor;
import lombok.Data;
import lombok.extern.slf4j.Slf4j;
import org.shecodes.course.aws_cloud.ifat.model.CourseImage;

@Slf4j
@Data
@AllArgsConstructor
public class CourseImageDTO {
    private String course_name;
    private String image_format;
    private String course_image;

    public CourseImageDTO(CourseImage courseImage){
        this.course_name=courseImage.getCourseName();
        this.image_format=courseImage.getImageFormat();
        this.course_image=courseImage.getCourseImage();
    }
}
